from sys import argv

inputfile,out,=argv[1:]
ccs_file = open(inputfile,"r")
outfile = open(out, "w")

wt = ""

for line in ccs_file:
    if len(line)>30: 
        mut_num = 0
        geno = line.strip().split(",")[1]
        for i in range(733):
            if geno[i] != wt[i]:
                mut_num += 1
        if mut_num == 1:
            for a in range(733):
                if geno[a] != wt[a]:
                    outfile.write(str(a) + "," + wt[a] + "," + geno[a] + "\n")

ccs_file.close()
outfile.close()